using LibrarieModele;using NivelStocareDate;using System;using System.Configuration;using System.Drawing;using System.IO;using System.Windows.Forms;namespace EvidentaStudenti_UI_WindowsForms{    public partial class Form1 : Form    {        AdministrareStudenti_FisierText adminStudenti;        private Label lblNume;        private Label lblPrenume;        private Label lblNote;        private Label[] lblsNume;        private Label[] lblsPrenume;        private Label[] lblsNote;        private TextBox txtNume;        private TextBox txtPrenume;        private TextBox txtNote;        private const int LATIME_CONTROL = 100;        private const int DIMENSIUNE_PAS_Y = 30;        private const int DIMENSIUNE_PAS_X = 120;        public Form1()        {            int nrStudentii;                        string numeFisier = ConfigurationManager.AppSettings["NumeFisier"];            string locatieFisierSolutie = Directory.GetParent(System.IO.Directory.GetCurrentDirectory()).Parent.Parent.FullName;            string caleCompletaFisier = locatieFisierSolutie + "\\" + numeFisier;            adminStudenti = new AdministrareStudenti_FisierText(caleCompletaFisier);            adminStudenti.GetStudenti(out nrStudentii);            InitializeComponent();                        //setare proprietati            this.Size = new Size(1000, 200);            this.StartPosition = FormStartPosition.Manual;            this.Location = new Point(100, 100);            this.Font = new Font("Arial", 9, FontStyle.Bold);            this.ForeColor = Color.LimeGreen;            this.Text = "Informatii studenti";            //adaugare control de tip Label pentru 'Nume';            lblNume = new Label();            lblNume.Width = LATIME_CONTROL;            lblNume.Text = "Nume";            lblNume.Left = DIMENSIUNE_PAS_X;            lblNume.ForeColor = Color.DarkGreen;            this.Controls.Add(lblNume);                        //adaugare control de tip TextBox pentru introducerea numelui student            txtNume = new TextBox();            txtNume.Width = LATIME_CONTROL;            txtNume.Left = DIMENSIUNE_PAS_X;            txtNume.Top = DIMENSIUNE_PAS_Y;            this.Controls.Add(txtNume);            //adaugare control de tip Label pentru 'Prenume';            lblPrenume = new Label();            lblPrenume.Width = LATIME_CONTROL;            lblPrenume.Text = "Prenume";            lblPrenume.Left = 2 * DIMENSIUNE_PAS_X;            lblPrenume.ForeColor = Color.DarkGreen;            this.Controls.Add(lblPrenume);                        //adaugare control de tip TextBox pentru introducerea prenumelui student            txtPrenume = new TextBox();            txtPrenume.Width = LATIME_CONTROL;            txtPrenume.Left = 2 * DIMENSIUNE_PAS_X;            txtPrenume.Top = DIMENSIUNE_PAS_Y;            this.Controls.Add(txtPrenume);            //adaugare control de tip Label pentru 'Note';            lblNote = new Label();            lblNote.Width = LATIME_CONTROL;            lblNote.Text = "Note";            lblNote.Left = 3 * DIMENSIUNE_PAS_X;            lblNote.ForeColor = Color.DarkGreen;            this.Controls.Add(lblNote);                                    //adaugare control de tip TextBox pentru introducerea notelor student            txtNote = new TextBox();            txtNote.Width = LATIME_CONTROL;            txtNote.Left = 3 * DIMENSIUNE_PAS_X;            txtNote.Top = DIMENSIUNE_PAS_Y;            this.Controls.Add(txtNote);                                    //adaugare control de tip Button pentru adaugarea unui student            Button btnAdauga = new Button();            btnAdauga.Width = LATIME_CONTROL;            btnAdauga.Text = "Adauga";            btnAdauga.Left = 4 * DIMENSIUNE_PAS_X;            btnAdauga.Top = DIMENSIUNE_PAS_Y;            btnAdauga.Click += (sender, args) =>            {                Student student = new Student();                student.Nume = txtNume.Text;                student.Prenume = txtPrenume.Text;                student.SetNote(txtNote.Text);                int idStudent = nrStudentii + 1;                student.IdStudent = idStudent;                adminStudenti.AddStudent(student);                txtNote.Text = "";                txtNume.Text = "";                txtPrenume.Text = "";            };            Controls.Add(btnAdauga);                                    // adaugare control de tip Button pentru reimprospatarea listei de studenti            Button btnAfiseaza = new Button();            btnAfiseaza.Width = LATIME_CONTROL;            btnAfiseaza.Text = "Reflesh";            btnAfiseaza.Left = 5 * DIMENSIUNE_PAS_X;            btnAfiseaza.Top = DIMENSIUNE_PAS_Y;            btnAfiseaza.Click += BtnReflesh_Click;            this.Controls.Add(btnAfiseaza);        }        private void BtnReflesh_Click(object sender, EventArgs e)        {            AfiseazaStudenti();        }        private void Form1_Load(object sender, EventArgs e)        {            AfiseazaStudenti();        }        private void AfiseazaStudenti()        {            Student[] studenti = adminStudenti.GetStudenti(out int nrStudenti);            lblsNume = new Label[nrStudenti];            lblsPrenume = new Label[nrStudenti];            lblsNote = new Label[nrStudenti];            int i = 0;            foreach (Student student in studenti)            {                //adaugare control de tip Label pentru numele studentilor;                lblsNume[i] = new Label();                lblsNume[i].Width = LATIME_CONTROL;                lblsNume[i].Text = student.Nume;                lblsNume[i].Left = DIMENSIUNE_PAS_X;                lblsNume[i].Top = (i + 1) * DIMENSIUNE_PAS_Y;                this.Controls.Add(lblsNume[i]);                //adaugare control de tip Label pentru prenumele studentilor                lblsPrenume[i] = new Label();                lblsPrenume[i].Width = LATIME_CONTROL;                lblsPrenume[i].Text = student.Prenume;                lblsPrenume[i].Left = 2 * DIMENSIUNE_PAS_X;                lblsPrenume[i].Top = (i + 1) * DIMENSIUNE_PAS_Y;                this.Controls.Add(lblsPrenume[i]);                //adaugare control de tip Label pentru notele studentilor                lblsNote[i] = new Label();                lblsNote[i].Width = LATIME_CONTROL;                lblsNote[i].Text = string.Join(" ", student.GetNote());                lblsNote[i].Left = 3 * DIMENSIUNE_PAS_X;                lblsNote[i].Top = (i + 1) * DIMENSIUNE_PAS_Y;                this.Controls.Add(lblsNote[i]);                i++;            }                    }    }}